import React, {useContext} from "react";
import { Card, CardBody, CardTitle } from "reactstrap";
import ItemContext from './Components/ItemContext'
import { Link } from "react-router-dom";

function Home() {
  const {snacks, drinks} = useContext(ItemContext)

  return (
    <section className="col-md-8">
      <Card>
        <CardBody className="text-center">
          <CardTitle>
            <h3 className="font-weight-bold">
              Welcome to Silicon Valley's premier dive cafe!
            </h3>
            <br />
          <Link to="/snacks"><h4>{snacks.length} Snack Items</h4></Link>
          <Link to="/drinks"><h4>{drinks.length} Drink Items</h4></Link>
          </CardTitle>
        </CardBody>
      <Link to="/snacks/new"><button>Add a snack</button></Link>
      <Link to="/drinks/new"><button>Add a drink</button></Link>
      </Card>
    </section>
  );
}

export default Home;
